import React from 'react';
import { useNavigate } from 'react-router-dom';

const BackButton = () => {
  const navigate = useNavigate();

  const handleBack = () => {
    // Navigate explicitly to the homepage
    navigate('/');
  };

  return (
    <button onClick={handleBack} className="back-button">
      Back
    </button>
  );
};

export default BackButton;
