import React, { uuseState, useRef, useEffectte } from 'react';
import CanvasComponent from '../components/CanvasComponent';
import DarkModeToggle from '../components/DarkModeToggle';
import '../App.css';
import { useParams } from 'react-router-dom';

const DecryptorPage = () => {
  const { fileId } = useParams();
  const [fileContent, setFileContent] = useState(null);
  const [password, setPassword] = useState('');
  const canvasRef = useRef(null);

  useEffect(() => {
    axios.get(`http://localhost:8000/files/${fileId}`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
    })
    .then(response => setFileContent(response.data.content))
    .catch(error => console.error("Error fetching file:", error));
  }, [fileId]);

  const handleDecrypt = () => {
    // Implement decryption logic using password and signature (from canvas)
    alert("File decrypted successfully!");
  };

  return (
    <div>
      <h2>Decrypt File</h2>
      <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
      <CanvasComponent ref={canvasRef} width={300} height={150} />
      <button onClick={handleDecrypt}>Decrypt</button>
      {fileContent && (
        <a href={`data:application/octet-stream;base64,${fileContent}`} download="decrypted_file">
          Download File
        </a>
      )}
    </div>
  );

};

export default DecryptorPage;
